import React, { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import { Button, FormGroup, FormControl } from "react-bootstrap";

function App() {
  const [text, setText] = useState('')
  const onSubmit = () => {
    // do something
    console.log(text)
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    function validateForm() {
      return email.length > 0 && password.length > 0;
    }

    function handleSubmit(event) {
      event.preventDefault();
    }
  }
  return (
    <div className="jumbotron container" >
      <div className="row">
      <form onSubmit={handleSubmit}>
        <FormGroup controlId="email" bsSize="large">
          <ControlLabel>Email</ControlLabel>
          <FormControl
            autoFocus
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <ControlLabel>Password</ControlLabel>
          <FormControl
            value={password}
            onChange={e => setPassword(e.target.value)}
            type="password"
          />
        </FormGroup>
        <Button block bsSize="large" disabled={!validateForm()} type="submit">
          Login
        </Button>
      </form>
        <div className="col-sm-4">
          <label className="display-5">Lütfen youtube linki yapıştırın:</label>
          <input className="form-control" type='text' value={text} onChange={e => setText(e.target.value)} placeholder="paste youtube link..."></input>
          <input className="btn btn-primary btn-block" type='submit' onClick={onSubmit} value='İndir'></input>
        </div>
      </div>
    </div>
  );
}

export default App;
